    private void txtiddivisiKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtiddivisiKeyReleased
        
          String key = txtiddivisi.getText();
        System.out.println(key);
        
        if (key !=""){
            searchData(key);
        }
        else
        {
            load_table();
        }
        
    }//GEN-LAST:event_txtiddivisiKeyReleased

    private void searchbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchbtnActionPerformed
        try{
            ResultSet rs =  Db.getData ("SELECT * FROM divisi WHERE id_divisi='"+txtiddivisi.getText()+"'");

            if (rs.next()){
                txtiddivisi.setText(rs.getString("id_divisi"));
                txtnamadivisi.setText(rs.getString("nama_divisi"));
                txthonordivisi.setText(rs.getString("honor_divisi"));
            }
            else{
                JOptionPane.showMessageDialog(null, "Data Tidak Ditemukan");
            }

        }
        catch (Exception e){
            e.printStackTrace();

        }
    }//GEN-LAST:event_searchbtnActionPerformed

