    private void JTableDivisiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_JTableDivisiMouseClicked
        // TODO add your handling code here:
        int baris = JTableDivisi.rowAtPoint(evt.getPoint());
        String id_divisi =JTableDivisi.getValueAt(baris, 0).toString();
        txtiddivisi.setText(id_divisi);      
        String nama_divisi =JTableDivisi.getValueAt(baris, 1).toString();
        txtnamadivisi.setText(nama_divisi);
        String honor_divisi =JTableDivisi.getValueAt(baris, 2).toString();
        txthonordivisi.setText(honor_divisi);
    }//GEN-LAST:event_JTableDivisiMouseClicked

