    private void load_table() {
        
        //membuat tampilan model table
        DefaultTableModel model = new DefaultTableModel();
        model.addColumn("ID DIVISI");
        model.addColumn("NAMA DIVISI");
        model.addColumn("HONOR DIVISI");
        
        //menampilkan data database ke dalam tabel
        try{
            String sql = "select *from divisi";
            java.sql.Connection conn= config.configDB();
            java.sql.Statement stm=conn.createStatement();
            java.sql.ResultSet res=stm.executeQuery(sql);
            while(res.next()){
               model.addRow(new 
                Object[]{res.getString(1),res.getString(2),res.getString(3)});
            }
            JTableDivisi.setModel(model);
        }catch (SQLException e){
            
        }
    }
    
    private void searchData(String keyID){
        try{
            Object [] listkolom = {"ID", "NAMA DIVISI", "HONOR DIVISI"};
            DefaultTableModel dtm = new DefaultTableModel(null, listkolom);
             JTableDivisi.setModel(dtm);
             Connection conn = (Connection)config.configDB();
             dtm.getDataVector().removeAllElements();
             ResultSet resu = conn.createStatement().executeQuery("SELECT * FROM divisi WHERE id_divisi LIKE '"+keyID+"%'");
             while(resu.next()){
                 Object[] data = {
                     resu.getString("id_divisi"),
                     resu.getString("nama_divisi"),
                     resu.getString("honor_divisi")
                     
                 };
                 dtm.addRow(data);
             }
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }

    private void kosong() {
        
        txtiddivisi.setText(null);
        txtnamadivisi.setText(null);
        txthonordivisi.setText(null); 
    }
