    private void searchbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchbtnActionPerformed
        try{
            ResultSet rs =  Db.getData ("SELECT * FROM jabatan WHERE id_jabatan='"+txtidjabatan.getText()+"'");

            if (rs.next()){
                txtjabatan.setText(rs.getString("jabatan"));
                txttunjanganjabatan.setText(rs.getString("tunj_jabatan"));
                txtperjdinas.setText(rs.getString("perj_dinas"));
            }
            else{
                 JOptionPane.showMessageDialog(null, "Data Tidak Ditemukan");
            }
           
        }
        catch (Exception e){
            e.printStackTrace();
            
        }

    }//GEN-LAST:event_searchbtnActionPerformed

    private void txtidjabatanKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtidjabatanKeyReleased
        // TODO add your handling code here:
        String key = txtidjabatan.getText();
        System.out.println(key);
        
        if (key !=""){
            searchData(key);
        }
        else
        {
            load_table();
        }
    }//GEN-LAST:event_txtidjabatanKeyReleased

