    private void JTableJabatanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_JTableJabatanMouseClicked
        // TODO add your handling code here:
         int baris = JTableJabatan.rowAtPoint(evt.getPoint());
        String id_jabatan =JTableJabatan.getValueAt(baris, 0).toString();
        txtidjabatan.setText(id_jabatan);      
        String jabatan =JTableJabatan.getValueAt(baris, 1).toString();
        txtjabatan.setText(jabatan);
        String tunj_jabatan =JTableJabatan.getValueAt(baris, 2).toString();
        txttunjanganjabatan.setText(tunj_jabatan);
        String perj_jabatan =JTableJabatan.getValueAt(baris, 3).toString();
        txtperjdinas.setText(perj_jabatan);
    }//GEN-LAST:event_JTableJabatanMouseClicked

