    private void load_table() {
        DefaultTableModel model = new DefaultTableModel();
        model.addColumn("ID JABATAN");
        model.addColumn("JABATAN");
        model.addColumn("TUNJANGAN JABATAN");
        model.addColumn("PERJALANAN DINAS");
        
        //menampilkan data database ke dalam tabel
        try{
            String sql = "select *from jabatan";
            java.sql.Connection conn= config.configDB();
            
            java.sql.Statement stm=conn.createStatement();
            java.sql.ResultSet res=stm.executeQuery(sql);
            while(res.next()){
               model.addRow(new 
                Object[]{res.getString(1),res.getString(2),res.getString(3),res.getString(4)});
            }
            JTableJabatan.setModel(model);
        }catch (SQLException e){
            
        }
    }
    
    private void searchData(String keyID){
        try{
            Object [] listkolom = {"ID", "JABATAN", "TUNJANGAN", "PERJALANAN"};
            DefaultTableModel dtm = new DefaultTableModel(null, listkolom);
             JTableJabatan.setModel(dtm);
             Connection conn = (Connection)config.configDB();
             dtm.getDataVector().removeAllElements();
             ResultSet resu = conn.createStatement().executeQuery("SELECT * FROM jabatan WHERE id_jabatan LIKE '"+keyID+"%'");
             while(resu.next()){
                 Object[] data = {
                     resu.getString("id_jabatan"),
                     resu.getString("jabatan"),
                     resu.getString("tunj_jabatan"),
                     resu.getString("perj_dinas")
                     
                 };
                 dtm.addRow(data);
             }
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }


    private void kosong() {
        txtidjabatan.setText(null);
        txtjabatan.setText(null);
        txttunjanganjabatan.setText(null);
        txtperjdinas.setText(null);
        
    }
