    private void tambahbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tambahbtnActionPerformed
        // TODO add your handling code here:
        try {
            String sql = "INSERT INTO pegawai VALUES ('"+txtidpegawai.getText()+"',"+ "'"+txtiddivisi.getText()+"','"+txtijabatan.getText()+"','"+txtnama.getText()+"','"+combolpeglo.getSelectedItem()+"','"+txtalamat.getText()+"','"+txtusia.getText()+"')";
            java.sql.Connection conn=(Connection)config.configDB();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Penyimpanan Data Berhasil");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());          
        }
        load_table();
        kosong();
    }//GEN-LAST:event_tambahbtnActionPerformed

    private void updatebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updatebtnActionPerformed
        // TODO add your handling code here:
        try {
            String sql ="UPDATE pegawai SET id_divisi = '"+txtiddivisi.getText()+"', "+ "id_jabatan = '"+txtijabatan.getText()+"', "+ "nama = '"+txtnama.getText()+"',"+ "golongan = '"+combolpeglo.getSelectedItem()+"', "+ "alamat = '"+txtalamat.getText()+"', "+ "usia = '"+txtusia.getText()+"' WHERE id_pegawai = '"+txtidpegawai.getText()+"'";
            java.sql.Connection conn=(Connection)config.configDB();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "data berhasil di edit");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Perubahan Data Gagal"+e.getMessage());
        }
        load_table();
        kosong();
    }//GEN-LAST:event_updatebtnActionPerformed

