    private void JTablePegawaiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_JTablePegawaiMouseClicked
        // TODO add your handling code here:
         int baris = JTablePegawai.rowAtPoint(evt.getPoint());
        String id_pegawai =JTablePegawai.getValueAt(baris, 0).toString();
        txtidpegawai.setText(id_pegawai);      
        String id_divisi =JTablePegawai.getValueAt(baris, 1).toString();
        txtiddivisi.setText(id_divisi);
        String id_jabatan =JTablePegawai.getValueAt(baris, 2).toString();
        txtijabatan.setText(id_jabatan);   
        String nama =JTablePegawai.getValueAt(baris, 3).toString();
        txtnama.setText(nama);  
        String golong =JTablePegawai.getValueAt(baris, 4).toString();
        combolpeglo.setSelectedItem(golong);              
        String alamat =JTablePegawai.getValueAt(baris, 5).toString();
        txtalamat.setText(alamat);
        String usia =JTablePegawai.getValueAt(baris, 6).toString();
        txtusia.setText(usia);
    }//GEN-LAST:event_JTablePegawaiMouseClicked

    private void txtidpegawaiKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtidpegawaiKeyReleased
        
         String key = txtidpegawai.getText();
        System.out.println(key);
        
        if (key !=""){
            searchData(key);
        }
        else
        {
            load_table();
        }
    }//GEN-LAST:event_txtidpegawaiKeyReleased

