    private void searchbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchbtnActionPerformed
        try{
            ResultSet rs =  Db.getData ("SELECT * FROM pegawai WHERE id_pegawai='"+txtidpegawai.getText()+"'");

            if (rs.next()){
                txtidpegawai.setText(rs.getString("id_pegawai"));
                txtiddivisi.setText(rs.getString("id_divisi"));
                txtijabatan.setText(rs.getString("id_jabatan"));
                txtnama.setText(rs.getString("nama"));
                combolpeglo.setSelectedItem(rs.getString("golongan"));
                txtalamat.setText(rs.getString("alamat"));
                txtusia.setText(rs.getString("usia"));
            }
            else{
                JOptionPane.showMessageDialog(null, "Data Tidak Ditemukan");
            }

        }
        catch (Exception e){
            e.printStackTrace();

        }
    }//GEN-LAST:event_searchbtnActionPerformed

