    private void load_table() {
        DefaultTableModel model = new DefaultTableModel();
        model.addColumn("ID PEGAWAI");
        model.addColumn("ID DIVISI");
        model.addColumn("ID JABATAN");
        model.addColumn("NAMA");
        model.addColumn("GOLONGAN");
        model.addColumn("ALAMAT");
        model.addColumn("USIA");
        
        
        //menampilkan data database ke dalam tabel
        try{
            String sql = "select *from pegawai";
            java.sql.Connection conn= config.configDB();
            
            java.sql.Statement stm=conn.createStatement();
            java.sql.ResultSet res=stm.executeQuery(sql);
            while(res.next()){
               model.addRow(new 
                Object[]{res.getString(1),res.getString(2),res.getString(3),res.getString(4),res.getString(5),res.getString(6),res.getString(7)});
            }
            JTablePegawai.setModel(model);
        }catch (SQLException e){
            
        }
    }
    
     private void searchData(String keyID){
        try{
            Object [] listkolom = {"ID PEGAWAI", "ID DIVISI", "ID JABTAN",  "NAMA PEGAWAI", "GOLONGAN", "ALAMAT", "USIA"};
            DefaultTableModel dtm = new DefaultTableModel(null, listkolom);
             JTablePegawai.setModel(dtm);
             Connection conn = (Connection)config.configDB();
             dtm.getDataVector().removeAllElements();
             ResultSet resu = conn.createStatement().executeQuery("SELECT * FROM pegawai WHERE id_pegawai LIKE '"+keyID+"%'");
             while(resu.next()){
                 Object[] data = {
                     resu.getString("id_pegawai"),
                     resu.getString("id_divisi"),
                     resu.getString("id_jabatan"),
                     resu.getString("nama"),
                     resu.getString("golongan"),
                     resu.getString("alamat"),
                     resu.getString("usia")
                 };
                 dtm.addRow(data);
             }
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }

    private void kosong() {
        txtidpegawai.setText(null);
        txtiddivisi.setText(null);
        txtijabatan.setText(null);
        txtnama.setText(null);
        txtalamat.setText(null);
        txtusia.setText(null);
        
    }
